import { React, useState, useEffect } from 'react';
import './Burger.css';
import icon1 from '../../../images/icon-menu1.svg';
import icon2 from '../../../images/icon-menu2.svg';

export default function Burger(props) {

  let [counter, setCounter] = useState(0);
  let [icon, setIcon] = useState(icon1);

  if (counter > 2) {
    setCounter(1);
  }

  const toggleMenu = () => {
    let container = document.getElementById('nav-container-open');
    let menutext = document.getElementById('menu-text');
    let cross = document.getElementById('burger-cross');

    if (counter === 1) {
      container.classList.add('nav-container-open');
      container.classList.remove('nav-container-close');
      menutext.classList.add('menu-text-off');
      menutext.classList.remove('menu-text-on');
      cross.classList.add('burger-cross-on');
      cross.classList.remove('burger-cross-off');
      setIcon(icon2);
    } else if (counter === 2) {
      container.classList.add('nav-container-close');
      container.classList.remove('nav-container-open');
      menutext.classList.add('menu-text-on');
      menutext.classList.remove('menu-text-off');
      cross.classList.add('burger-cross-off');
      cross.classList.remove('burger-cross-on');
      setIcon(icon1);
    }
  }

  useEffect(() => {
    toggleMenu();
  }, [counter]);


  return (
    <div className='hamburger-container'>
      <p id = 'menu-text' className='menu-text-on' onClick={() => setCounter(counter + 1)}>MENU</p>
      <img alt='icon menu' src={icon} id='burger-cross' className='burger-cross-off' onClick={() => setCounter(counter + 1)}></img>
    </div>
  )
}
