import React from 'react'
import './Gallery1.css'

import image1 from '../../images/bolcom.png';
import image2 from '../../images/kempen.png';
import image3 from "../../images/philips.png";
import image4 from "../../images/gemeentemuseum.png";


export default function Gallery1() {

    const image = [
        {src:image1, alt: 'image1', id:'image1'},
        {src:image2, alt: 'image2', id:'image2'},
        {src:image3, alt: 'image3', id:'image3'},
        {src:image4, alt: 'image4', id:'image4'},      
    ];

    const description = [
        {company:'BOLCOM', text: 'A summer island in the Netherlands', action:'VIEW CASE'},
        {company:'KEMPEN', text: 'Not some average banking website', action:'VIEW CASE'},
        {company:'PHILIPS', text: 'Beautiful design meets innovative technology', action:'VIEW CASE'},
        {company:'GEMEENTEMUSEUM', text: 'A 100 years of Mondriaan & De Stijl', action:'VIEW CASE'},      
    ];

    const imageShow = image.map(image=>{
        return (
        <div className="g1-wrapper">
            <li className="g1-li">
               <img className="g1-img" src = {image.src} alt={image.id}></img>
               <p  className="g1-p">{description.company}</p>
               <p  className="g1-p">{description.text}</p>
            </li>
        </div>
        )
    });

    return (
        <div className='g1-container'>
            <ul className="g1-ul">{imageShow}</ul>
        </div>
    )
}
