import { React, useState, useEffect } from 'react'
import './Section1.css'

import header from '../../images/header.jpg';

export default function Section1(props) {

  let [counterScroll, setCounterScroll] = useState(0);

  let navcontainer = document.getElementById('section1-container');


  const handleScroll = () => {
    if(window.pageYOffset===0){
      setCounterScroll(1);
    } else {
      setCounterScroll(0);
    }
  }

  function toggleOpacity(){
    if(counterScroll === 0){
      console.log(navcontainer);
      //navcontainer.style.backgroundColor='transparent';
    }

    if(counterScroll === 1){
      //navcontainer.style.backgroundColor='white';
    }
  }

  useEffect(() => {
    toggleOpacity();
  }, [counterScroll]);


  return (
    <section id='section1-container' className="section1-container" onScroll={()=>handleScroll()}>
        <img className='header-image' alt='' key='header'/>
        <h1 className='header-heading'>WORK</h1>
        <button className='header-button'>VIEW CASE</button>
    </section>

  )
}
