import React from 'react'
import './Gallery4.css'

import image1 from '../../images/chocomel.png';
import image2 from '../../images/jbl.png';
import image3 from "../../images/zalando.png";
import image4 from "../../images/koninklijke-bibliotheek.png"
import icon from "../../icons/icon-dropdown.svg"


export default function Gallery4() {

    const card = [
        {src:image1, company:'CHOCOMEL', text: 'A campaign for the limited edition letter packs', action:'VIEW CASE'},      
    ];

    const cardShow = card.map(card=>{
        return (
        <div className="g4-wrapper">
            <li className="g4-li">
               <img className="g4-img" src = {card.src} alt=''></img>
               <p  className="g4-company">{card.company}</p>
               <p  className="g4-text">{card.text}</p>
               <div className='g4-action-container'>
               <svg className='g4-icon' width="9" height="7" xmlns="http://www.w3.org/2000/svg">
                   <path fill="blue" d="M0 0l4.505 7L9 0z" fill-rule="evenodd"/>
               </svg>
                    <p className='g4-action'>VIEW CASE</p>
               </div>
            </li>
        </div>
        )
    });

    return (
        <section className='g4-container'>
            <ul className="g4-ul">{cardShow}</ul>
        </section>
    )
}
