import { React, useState, useEffect } from 'react'
import './Nav.css'

import PageList from './PageList/PageList.js'
import MediaList from './MediaList/MediaList.js'
import LanguageList from './LanguageList/LanguageList';
import Logo from './Logo/Logo';
import Burger from './Burger/Burger';

export default function Navbar(props) {

  useEffect(()=>{

    window.addEventListener('scroll', scrollAnimate);

    function scrollAnimate(){
        let container = document.getElementById('section1-container');
        let navcontainer = document.getElementById('nav-container');

        if (window.pageYOffset === 0){
            navcontainer.style.backgroundColor='transparent';
        } else {
            navcontainer.style.backgroundColor='white';
        }
      }

return ()=>window.removeEventListener('scroll',scrollAnimate);
})

  return (

    <section id='nav-container' className="nav-container">

      <div className='nav-bar'>
      <Logo />
      <Burger />
      </div>

      <div id='nav-container-open' className='nav-container-close'>
      <LanguageList />
      <PageList />
      <MediaList />
      </div>

    </section>

  )
}
