import {React, useState, useEffect} from 'react'
import './Nav.css'
import icon1 from '../images/icon-menu1.svg';
import icon2 from '../images/icon-menu2.svg';

import PageList from './PageList.js'
import MediaList from './MediaList.js'
import LanguageList from './LanguageList';
import Logo from './Logo';
import Burger from './Burger';

export default function Navbar(props){

  let [counter, setCounter] = useState(0);
  let [icon, setIcon] = useState(icon1);

  if(counter>2){
    setCounter(1);
  }

  const toggleMenu = () => {  
    console.log(counter);
    let hamburger = document.getElementById('nav-container-open');
    if(counter === 1){
        hamburger.classList.add('nav-container-open');
        hamburger.classList.remove('nav-container-close');
        setIcon(icon2);
    } else if (counter === 2){
        hamburger.classList.add('nav-container-close');
        hamburger.classList.remove('nav-container-open');
        setIcon(icon1);
    } 
  }

  useEffect(() => {
    toggleMenu();
  },[counter]);







    return(

    <section id ='nav-container' className="nav-container">

    <div className='nav-bar'></div>
    <Logo/>
    <Burger/>




    <div id ='nav-container-open' className='nav-container-close'>

    {/* LANGUAGE LIST */}
    <LanguageList/>

    {/* PAGE LIST */}
    <PageList/>

    {/* SOCIAL MEDIA LIST */}
     <MediaList/>
      </div>

    </section>

   )
 }
