import { React, useState, useEffect } from 'react'
import './Section1.css'

import header from '../../images/header.jpg';

export default function Section1(props) {

  useEffect(()=>{

    window.addEventListener('scroll', scrollAnimate);

    function scrollAnimate(){
        let container = document.getElementById('header-image');

        if (window.pageYOffset === 0){
            container.classList.remove('info1-container-start');
            container.classList.add('info1-container');
        } else {
            container.classList.remove('info1-container');
            container.classList.add('info1-container-start');
        }
      }

return ()=>window.removeEventListener('scroll',scrollAnimate);
})

  return (
    <section id='section1-container' className="section1-container">
        <img id='header-image' className='header-image' alt='' key='header'/>
        <h1 className='header-heading'>WORK</h1>
        <button className='header-button'>VIEW CASE</button>
    </section>

  )
}
