import { React, useState, useEffect } from 'react';
import './Burger.css';
import icon1 from '../../../images/icon-menu1.svg';
import icon2 from '../../../images/icon-menu2.svg';

export default function Burger(props) {

  let [counter, setCounter] = useState(0);
  let [icon, setIcon] = useState(icon1);

  if (counter > 2) {
    setCounter(1);
  }

  const toggleMenu = () => {
    let container = document.getElementById('nav-container-open');
    if (counter === 1) {
      container.classList.add('nav-container-open');
      container.classList.remove('nav-container-close');
      setIcon(icon2);
    } else if (counter === 2) {
      container.classList.add('nav-container-close');
      container.classList.remove('nav-container-open');
      setIcon(icon1);
    }
  }

  useEffect(() => {
    toggleMenu();
  }, [counter]);


  return (
    <div className='hamburger-container'>
      <p className='menu-text'>MENU</p>
      <img alt='icon menu' src={icon} className='hamburguer' onClick={() => setCounter(counter + 1)}></img>
    </div>
  )
}
