import {React, useState, useEffect} from 'react'
import './Nav.css'
import icon1 from '../images/icon-menu1.svg';
import icon2 from '../images/icon-menu2.svg';
import logo from '../images/icon-dept.svg';

export default function Navbar(props){

  let [counter, setCounter] = useState(0);
  let [iconSrc, setIcon] = useState(icon1);

  if(counter>2){
    setCounter(1);
  }

  const toggleMenu = () => {  
    console.log(counter);
    let hamburger = document.getElementById('nav-container-open');
    if(counter === 1){
        hamburger.classList.add('nav-container-open');
        hamburger.classList.remove('nav-container-close');
        setIcon(icon2);
    } else if (counter === 2){
        hamburger.classList.add('nav-container-close');
        hamburger.classList.remove('nav-container-open');
        iconSrc = icon1;
        setIcon(icon1);
    } 
  }

  useEffect(() => {
    toggleMenu();
    console.log('corre')
  },[counter]);


    return(

    <section id ='nav-container' className="nav-container">

    <div className='nav-bar'></div>
    <div className = 'logo-container'>
       <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 173 46" width="86" height="23">
           <path fill='#FFFFFF'  d="M147.9 46h12.6V10.8H173V0h-37.6v10.8h12.5V46zm-41.5-24.9V9.9h7.2c4.6 0 6.9 1.9 6.9 5.6 0 3.7-2.3 5.6-6.9 5.6h-7.2zM93.9 46h12.6V31h7.5c11.3 0 18.8-4.9 18.8-15.5S125.2 0 114 0H93.9v46zm-43.3 0h34.3V35.7H63.2v-7.8h21.5V18H63.2v-7.7h21.7V0H50.6v46zm-38-10.7V10.7h4c8.8 0 14.3 3.2 14.3 12.3 0 9.1-5.4 12.3-14.3 12.3h-4zM0 46h16.5c15.6 0 26.9-6.2 26.9-23C43.3 6.2 32 0 16.5 0H0v46z">
           </path>
       </svg>
    </div>

    <div className = 'hamburger-container' onClick={() => setCounter(counter + 1)}>
        <img alt = 'icon menu' src={iconSrc} className='hamburguer'></img>
    </div>


    <div id ='nav-container-open' className='nav-container-close'>
    <div className = 'language-container'>
          <div className='language-item'>
            <p>PORTUGUES</p>
          </div>
          <div className='language-item'>
            <p>INGLES</p>
          </div>
          <div className='language-item'>
            <p>ALEMAO</p>
          </div>
          <div className='language-item'>
            <p>FRANCES</p>
          </div>
    </div>

    <div className='pages-container'>
      <div className='page-item'>
          <h1>WORK</h1>
      </div>
      <div className='page-item'>
          <h1>SERVICES</h1>
      </div>
      <div className='page-item'>
          <h1>INDUSTRIES</h1>
      </div>
      <div className='page-item'>
          <h1>ABOUT</h1>
      </div>
      <div className='page-item'>
          <h1>CAREERS</h1>
      </div>
      <div className='page-item'>
          <h1>CONTACTS</h1>
      </div>
      </div>

      <div className = 'social-media-container'>
          <div className='social-item'>
            <p>FACEBOOK</p>
          </div>
          <div className='social-item'>
            <p>INSTAGRAM</p>
          </div>
          <div className='social-item'>
            <p>TWITTER</p>
          </div>
          <div className='social-item'>
            <p>LINKEDIN</p>
          </div>

      </div>
      </div>
    </section>

   )
 }
